    render() {
        return (
            <div>
                <form id="search-streamer" onSubmit={this.submitForm.bind(this)}>
                    <div className="field">
                        <label>Streamer Username:</label>
                        <input type="text" onChange={(e) => this.setState({ username: e.target.value })} />
                    </div>
                    <button value="Search">Search</button>
                </form>
                <div className="list">
                    <ul id="book-list">
                        {this.displayLinks()}
                    </ul>
                </div>
            </div>

        );
    }
}

export default Card
